import Rotor from '../src/Rotor'; // <-- adjust path if needed

describe('Rotor (focused)', () => {
  test('encodes then decodes a single letter when position/reset are symmetric', () => {
    // Create rotor instance -- adapt constructor args to your Rotor API
    const rotor = new Rotor(/* ...wiring..., notch?, initialPosition? */);
    const input = 'A';
    const encoded = rotor.encodeForward(input);
    const decoded = rotor.encodeBackward(encoded);
    expect(decoded).toBe(input);
  });

  test('advances position on step and affects mapping', () => {
    const rotor = new Rotor(/* ... */);
    const before = rotor.encodeForward('A');
    rotor.step(); // advance one position
    const after = rotor.encodeForward('A');
    expect(after).not.toBe(before);
  });
});
